function ValUI.SetScaleformParams(scaleform, data)
    data = data or {}
    for k, v in pairs(data) do
        PushScaleformMovieFunction(scaleform, v.name)
        if v.param then
            for _, par in pairs(v.param) do
                if math.type(par) == "integer" then
                    PushScaleformMovieFunctionParameterInt(par)
                elseif type(par) == "boolean" then
                    PushScaleformMovieFunctionParameterBool(par)
                elseif math.type(par) == "float" then
                    PushScaleformMovieFunctionParameterFloat(par)
                elseif type(par) == "string" then
                    PushScaleformMovieFunctionParameterString(par)
                end
            end
        end
        if v.func then
            v.func()
        end
        PopScaleformMovieFunctionVoid()
    end
end

---Visible
---@param Menu function
---@param Value boolean
---@return table
---@public
function ValUI.Visible(Menu, Value)
    if Menu ~= nil then
        if Menu() then
            if type(Value) == "boolean" then
                if Value then
                    if ValUI.CurrentMenu ~= nil then
                        ValUI.CurrentMenu.Open = not Value
                    end
                    Menu:UpdateInstructionalButtons(Value);
                    ValUI.CurrentMenu = Menu
                    menuOpen = true
                else
                    menuOpen = false
                    ValUI.CurrentMenu = nil
                end
                Menu.Open = Value
                ValUI.Options = 0
                ValUI.ItemOffset = 0
                ValUI.LastControl = false
            else
                return Menu.Open
            end
        end
    end
end

function ValUI.CloseAll()
    menuOpen = false
    if ValUI.CurrentMenu ~= nil then
        local parent = ValUI.CurrentMenu.Parent
        while parent ~= nil do
            parent.Index = 1
            parent.Pagination.Minimum = 1
            parent.Pagination.Maximum = 10
            parent = parent.Parent
        end
        ValUI.CurrentMenu.Index = 1
        ValUI.CurrentMenu.Pagination.Minimum = 1
        ValUI.CurrentMenu.Pagination.Maximum = 10
        ValUI.CurrentMenu.Open = false
        ValUI.CurrentMenu = nil
    end
    ValUI.Options = 0
    ValUI.ItemOffset = 0
	ResetScriptGfxAlign()
end

