function ValUI.Banner(Enabled, Glare)
    if type(Enabled) == "boolean" then
        if Enabled == true then


            if ValUI.CurrentMenu ~= nil then
                if ValUI.CurrentMenu() then


                    ValUI.ItemsSafeZone(ValUI.CurrentMenu)

                    if ValUI.CurrentMenu.Sprite then
                        RenderSprite(ValUI.CurrentMenu.Sprite.Dictionary, ValUI.CurrentMenu.Sprite.Texture, ValUI.CurrentMenu.X, ValUI.CurrentMenu.Y, ValUI.Settings.Items.Title.Background.Width + ValUI.CurrentMenu.WidthOffset, ValUI.Settings.Items.Title.Background.Height)
                    else
                        RenderRectangle(ValUI.CurrentMenu.X, ValUI.CurrentMenu.Y, ValUI.Settings.Items.Title.Background.Width + ValUI.CurrentMenu.WidthOffset, ValUI.Settings.Items.Title.Background.Height, ValUI.CurrentMenu.Rectangle.R, ValUI.CurrentMenu.Rectangle.G, ValUI.CurrentMenu.Rectangle.B, ValUI.CurrentMenu.Rectangle.A)
                    end

                    if Glare then

                        local ScaleformMovie = RequestScaleformMovie("MP_MENU_GLARE")
                        if not HasScaleformMovieLoaded(ScaleformMovie) then
                            ScaleformMovie = RequestScaleformMovie("MP_MENU_GLARE")
                            while not HasScaleformMovieLoaded(ScaleformMovie) do
                                Citizen.Wait(0)
                            end
                        end

                        ---@type number
                        local Glarewidth = ValUI.Settings.Items.Title.Background.Width + ValUI.CurrentMenu.WidthOffset
                        ---@type number
                        local Glareheight = ValUI.Settings.Items.Title.Background.Height
                        ---@type number
                        local GlareX = ValUI.CurrentMenu.X / 1860 + ValUI.CurrentMenu.SafeZoneSize.X / 53.211
                        ---@type number
                        local GalreY = ValUI.CurrentMenu.Y / 1080 + ValUI.CurrentMenu.SafeZoneSize.Y / 33.195020746888

                        ValUI.SetScaleformParams(ScaleformMovie, {
                            { name = "SET_DATA_SLOT", param = { GetGameplayCamRelativeHeading() } }
                        })

                        DrawScaleformMovie(ScaleformMovie, GlareX, GalreY, Glarewidth / 430, Glareheight / 100, 255, 255, 255, 255, 0)

                    end

                    RenderText(ValUI.CurrentMenu.Title, ValUI.CurrentMenu.X + ValUI.Settings.Items.Title.Text.X + (ValUI.CurrentMenu.WidthOffset / 2), ValUI.CurrentMenu.Y + ValUI.Settings.Items.Title.Text.Y, 1, ValUI.Settings.Items.Title.Text.Scale, 255, 255, 255, 255, 1)
                    ValUI.ItemOffset = ValUI.ItemOffset + ValUI.Settings.Items.Title.Background.Height
                end
            end
        end
    else
        error("Enabled is not boolean")
    end
end

