---Subtitle
---@return nil
---@public
function ValUI.Subtitle()
    if ValUI.CurrentMenu ~= nil then
        if ValUI.CurrentMenu() then
            ValUI.ItemsSafeZone(ValUI.CurrentMenu)
            if ValUI.CurrentMenu.Subtitle ~= "" then
                RenderRectangle(ValUI.CurrentMenu.X, ValUI.CurrentMenu.Y + ValUI.ItemOffset, ValUI.Settings.Items.Subtitle.Background.Width + ValUI.CurrentMenu.WidthOffset, ValUI.Settings.Items.Subtitle.Background.Height + ValUI.CurrentMenu.SubtitleHeight, 0, 0, 0, 255)
                RenderText(ValUI.CurrentMenu.Subtitle, ValUI.CurrentMenu.X + ValUI.Settings.Items.Subtitle.Text.X, ValUI.CurrentMenu.Y + ValUI.Settings.Items.Subtitle.Text.Y + ValUI.ItemOffset, 0, 0.26, 245, 245, 245, 255, nil, false, false, ValUI.Settings.Items.Subtitle.Background.Width + ValUI.CurrentMenu.WidthOffset)
                if ValUI.CurrentMenu.Index > ValUI.CurrentMenu.Options or ValUI.CurrentMenu.Index < 0 then
                    ValUI.CurrentMenu.Index = 1
                end
                ValUI.RefreshPagination()
                if ValUI.CurrentMenu.PageCounter == nil then
                    RenderText(ValUI.CurrentMenu.PageCounterColour .. ValUI.CurrentMenu.Index .. " / " .. ValUI.CurrentMenu.Options, ValUI.CurrentMenu.X + ValUI.Settings.Items.Subtitle.PreText.X + ValUI.CurrentMenu.WidthOffset, ValUI.CurrentMenu.Y + ValUI.Settings.Items.Subtitle.PreText.Y + ValUI.ItemOffset, 0, 0.26, 245, 245, 245, 255, 2)
                else
                    RenderText(ValUI.CurrentMenu.PageCounter, ValUI.CurrentMenu.X + ValUI.Settings.Items.Subtitle.PreText.X + ValUI.CurrentMenu.WidthOffset, ValUI.CurrentMenu.Y + ValUI.Settings.Items.Subtitle.PreText.Y + ValUI.ItemOffset, 0, 0.26, 245, 245, 245, 255, 2)
                end
                ValUI.ItemOffset = ValUI.ItemOffset + ValUI.Settings.Items.Subtitle.Background.Height
            end
        end
    end
end

