---Render
---@param instructionalButton boolean
---@return nil
---@public
function ValUI.Render(instructionalButton)
    if ValUI.CurrentMenu ~= nil then
        if ValUI.CurrentMenu() then
            if ValUI.CurrentMenu.Safezone then
                ResetScriptGfxAlign()
            end
            if (instructionalButton) then
                DrawScaleformMovieFullscreen(ValUI.CurrentMenu.InstructionalScaleform, 255, 255, 255, 255, 0)
            end
            ValUI.CurrentMenu.Options = ValUI.Options
            ValUI.CurrentMenu.SafeZoneSize = nil
            ValUI.Controls()
            ValUI.Options = 0
            ValUI.StatisticPanelCount = 0
            ValUI.ItemOffset = 0
            if ValUI.CurrentMenu.Controls.Back.Enabled and ValUI.CurrentMenu.Closable then
                if ValUI.CurrentMenu.Controls.Back.Pressed then
                    ValUI.CurrentMenu.Controls.Back.Pressed = false
                    local Audio = ValUI.Settings.Audio
                    ValUI.PlaySound(Audio[Audio.Use].Back.audioName, Audio[Audio.Use].Back.audioRef)
                    if ValUI.CurrentMenu.Closed ~= nil then
                        ValUI.CurrentMenu.Closed()
                    end
                    if ValUI.CurrentMenu.Parent ~= nil then
                        if ValUI.CurrentMenu.Parent() then
                            ValUI.NextMenu = ValUI.CurrentMenu.Parent
                        else
                            ValUI.NextMenu = nil
                            ValUI.Visible(ValUI.CurrentMenu, false)
                        end
                    else
                        ValUI.NextMenu = nil
                        ValUI.Visible(ValUI.CurrentMenu, false)
                    end
                end
            end
            if ValUI.NextMenu ~= nil then
                if ValUI.NextMenu() then
                    ValUI.Visible(ValUI.CurrentMenu, false)
                    ValUI.Visible(ValUI.NextMenu, true)
                    ValUI.CurrentMenu.Controls.Select.Active = false
                    ValUI.NextMenu = nil
                    ValUI.LastControl = false
                end
            end
        end
    end
end

---ItemsDescription
---@param CurrentMenu table
---@param Description string
---@param Selected boolean
---@return nil
---@public
function ValUI.ItemsDescription(CurrentMenu, Description, Selected)
    ---@type table
    if Description ~= "" or Description ~= nil then
        local SettingsDescription = ValUI.Settings.Items.Description;
        if Selected and CurrentMenu.Description ~= Description then
            CurrentMenu.Description = Description or nil
            ---@type number
            local DescriptionLineCount = GetLineCount(CurrentMenu.Description, CurrentMenu.X + SettingsDescription.Text.X, CurrentMenu.Y + SettingsDescription.Text.Y + CurrentMenu.SubtitleHeight + ValUI.ItemOffset, 0, SettingsDescription.Text.Scale, 255, 255, 255, 255, nil, false, false, SettingsDescription.Background.Width + CurrentMenu.WidthOffset)
            if DescriptionLineCount > 1 then
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height * DescriptionLineCount
            else
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height + 7
            end
        end
    end
end

---MouseBounds
---@param CurrentMenu table
---@param Selected boolean
---@param Option number
---@param SettingsButton table
---@return boolean
---@public
function ValUI.ItemsMouseBounds(CurrentMenu, Selected, Option, SettingsButton)
    ---@type boolean
    local Hovered = false
    Hovered = ValUI.IsMouseInBounds(CurrentMenu.X + CurrentMenu.SafeZoneSize.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SafeZoneSize.Y + CurrentMenu.SubtitleHeight + ValUI.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height)
    if Hovered and not Selected then
        RenderRectangle(CurrentMenu.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SubtitleHeight + ValUI.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height, 255, 255, 255, 20)
        if CurrentMenu.Controls.Click.Active then
            CurrentMenu.Index = Option
            local Audio = ValUI.Settings.Audio
            ValUI.PlaySound(Audio[Audio.Use].Error.audioName, Audio[Audio.Use].Error.audioRef)
        end
    end
    return Hovered;
end

---ItemsSafeZone
---@param CurrentMenu table
---@return nil
---@public
function ValUI.ItemsSafeZone(CurrentMenu)
    if not CurrentMenu.SafeZoneSize then
        CurrentMenu.SafeZoneSize = { X = 0, Y = 0 }
        if CurrentMenu.Safezone then
            CurrentMenu.SafeZoneSize = ValUI.GetSafeZoneBounds()
            SetScriptGfxAlign(76, 84)
            SetScriptGfxAlignParams(0, 0, 0, 0)
        end
    end
end

function ValUI.CurrentIsEqualTo(Current, To, Style, DefaultStyle)
    if (Current == To) then
        return Style;
    else
        return DefaultStyle or {};
    end
end

function ValUI.RefreshPagination()
    if (ValUI.CurrentMenu ~= nil) then
        if (ValUI.CurrentMenu.Index > 10) then
            local offset = ValUI.CurrentMenu.Index - 10
            ValUI.CurrentMenu.Pagination.Minimum = 1 + offset
            ValUI.CurrentMenu.Pagination.Maximum = 10 + offset
        else
            ValUI.CurrentMenu.Pagination.Minimum = 1
            ValUI.CurrentMenu.Pagination.Maximum = 10
        end
    end
end

function ValUI.IsVisible(menu, header, glare, instructional, items, panels)
    if (ValUI.Visible(menu)) then
        if (header == true) then
            ValUI.Banner(true, false)
        end
        ValUI.Subtitle()
        if (items ~= nil) then
            items()
        end
        ValUI.Background();
        ValUI.Navigation();
        ValUI.Description();
        if (panels ~= nil) then
            panels()
        end
        ValUI.Render(instructional or false)
    end
end

function ValUI.CreateWhile(wait, menu, key, closure)
    Citizen.CreateThread(function()
        while (true) do
            Citizen.Wait(wait or 0.1)

            if(key ~= nil)then
                if IsControlJustPressed(1, key) then
                    ValUI.Visible(menu, not ValUI.Visible(menu))
                end
            end

            closure()
        end
    end)
end

